import React from "react";  

class ToDo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      completed: this.props.task.completed,
    };
  }

  toggleCompletion = () => {
    this.setState((prevState) => ({
      completed: !prevState.completed,
    }));
    this.props.onToggleCompleted(this.props.task.id);
  };

  render() {
    const { task } = this.props;
    return (
      <li className={"list-group-item d-flex justify-content-between align-items-center border-0 border-bottom py-3 fs-5 bg-white" + 
        (this.state.completed ? " text-decoration-line-through text-secondary" : "")}>
        {task.name}
        <button 
          className={"btn btn-sm rounded-pill px-3 shadow " + (this.state.completed ? "btn-success" : "btn-outline-success")}
          onClick={this.toggleCompletion}
        >
          &#x2713;
        </button>
      </li>
    );
  }
}

export default ToDo;
