import React from "react";

class AddTask extends React.Component {
  state = {
    taskName: ""
  };

  handleChange = (e) => {
    this.setState({ taskName: e.target.value });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    if (this.state.taskName.trim() && this.props.onAddTask) {
      this.props.onAddTask(this.state.taskName.trim());
      this.setState({ taskName: "" });
    }
  };

  render() {
    return (
      <div className="card shadow add-task-card mx-auto" style={{maxWidth: 480}}>
        <form className="card-body" onSubmit={this.handleSubmit}>
          <div className="form-group mb-4">
            <label
              htmlFor="taskName"
              className="form-label fw-semibold text-primary"
              style={{ fontSize: "1.15rem" }}
            >
              Nom de la t√¢che
            </label>
            <input
              type="text"
              className="form-control form-control-lg border-2 border-primary add-task-input"
              id="taskName"
              placeholder="Ex : Acheter du lait"
              required
              value={this.state.taskName}
              onChange={this.handleChange}
            />
          </div>
          <div className="d-grid">
            <button
              type="submit"
              className="btn btn-primary btn-lg fw-semibold shadow-sm add-task-btn"
            >
              Ajouter
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default AddTask;
