import React from "react";
import ToDo from "./ToDo";

const ToDoList = ({tasks, match}) => {
    let filteredTasks

    switch (match.params.filter) {
        case "completed":
            filteredTasks = tasks.filter(task => task.completed);
            break;
        default:
            filteredTasks = tasks;
    }
    if (filteredTasks.length === 0) {
        return(
              <>
        <h1 className="m-3 text-success fw-bold display-5 text-center mb-4">游늶 Liste de t칙ches</h1>
        <ul className="list-group m-3 shadow-sm rounded">
          <li className="list-group-item text-center">
            <h2 className="text-secondary">Aucune t칙che  afficher</h2>
            <p className="text-muted">Ajoutez une nouvelle t칙che pour commencer !</p> 
            </li>  
        </ul>
    </>
            
        )
    }else {
    return (
    <>
        <h1 className="m-3 text-success fw-bold display-5 text-center mb-4">游늶 Liste de t칙ches</h1>
        <ul className="list-group m-3 shadow-sm rounded">
            {
                tasks.map((task) => (
                <ToDo key={task.id} task={task} />
                ))
            }
        </ul>
    </>
    )
}
}

export default ToDoList;
