import React from "react";  

class ToDo extends React.Component {
    state = {
        completed: this.props.task.completed,
    };

    toogleCompletion = () => {
        this.setState((prevState) => ({
            completed: !prevState.completed,
        }));
    }

  render() {
    return (
       <li className="list-group-item d-flex justify-content-between align-items-center border-0 border-bottom py-3 fs-5 bg-white">
                {this.props.task.name}
                <button className="btn btn-sm btn-outline-success rounded-pill px-3 shadow" on>&#x2713;</button>
            </li>
    );
  }
}

export default ToDo;
