import React from "react";
import ToDoList from "./ToDoList";
import AddTask from "./AddTask";
import NavBar from "./NavBar";
import initialData from "../initialData";
import { Routes, Route, useNavigate, useLocation } from "react-router-dom";
import "../css/toDo.css";

// Fonction pour dÃ©terminer le titre selon la page courante
function getPageTitle(pathname) {
  switch (pathname) {
    case "/":
    case "/active":
      return "ðŸ“‹ Liste des tÃ¢ches";
    case "/completed":
      return "âœ… TÃ¢ches terminÃ©es";
    case "/add-task":
      return "ðŸ“ Nouvelle tÃ¢che";
    default:
      return "ðŸ“‹ Liste des tÃ¢ches";
  }
}

// Filtrage des tÃ¢ches selon l'URL
function useQueryFilter(tasks) {
  const location = useLocation();
  if (location.pathname === "/completed") {
    return tasks.filter(t => t.completed);
  }
  if (location.pathname === "/active") {
    return tasks.filter(t => !t.completed);
  }
  // Par dÃ©faut ("/" ou autre)
  return tasks;
}

function AppWrapper() {
  const [tasks, setTasks] = React.useState(initialData);
  const location = useLocation();
  const filteredTasks = useQueryFilter(tasks);
  const navigate = useNavigate();

  const handleAddTask = (name) => {
    setTasks(prev =>
      [
        ...prev,
        {
          id: Date.now().toString(36) + Math.random().toString(36).substr(2, 5),
          name,
          completed: false,
        }
      ]);
    navigate("/");
  };

  const handleToggleCompleted = (id) => {
    setTasks(prev =>
      prev.map(task =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const handleClearCompleted = () => {
    setTasks(prev => prev.filter(task => !task.completed));
  };

  // Titre dynamique selon la page
  const pageTitle = getPageTitle(location.pathname);

  return (
    <section id="todo" className="container py-5 mb-5">
      <h1 className="m-3 fw-bold display-5 text-center mb-4">{pageTitle}</h1>
      <Routes>
        <Route path="/add-task" element={
          <AddTask onAddTask={handleAddTask} />
        } />
        <Route path={["/", "/active", "/completed"]} element={
          <ToDoList
            tasks={filteredTasks}
            onToggleCompleted={handleToggleCompleted}
          />
        } />
      </Routes>
      <NavBar onClearCompleted={handleClearCompleted} />
    </section>
  );
}

export default App;
